INSERT INTO `zx_course` (`id`, `uid`, `cid`, `tid`, `free`, `title`, `theme`, `tag`, `content`, `des`, `pic`, `click`, `recommend`, `language`, `price`, `video`, `time`, `agree`, `thank`, `addtime`) VALUES
(1, 0, 49, 1, '2', '创业课程:挣谁的钱', '人脉建设的要点', '淘宝，测试', '<p>商学院都是扯淡，非商学院争取不扯淡。谁告诉你人群是按低收入高收入分的？人群划分要按拥有社会资源的状况分！理解商业本质，洞察行业内涵，适合中国市场环境。每课内容简短，若有疑问请在首页提交问题，会有人来回答。</p>', '96485132', 'upload/201406/1401760832.png', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 21, 8, '2014-05-26 08:56:24'),
(2, 0, 49, 1, '2', '创业课程:挣什么钱', '人脉建设的要点', '测试，淘宝', '<p>商学院都是扯淡，非商学院争取不扯淡。谁都知道要抓住刚性需求？但为什么一出手就没抓住刚性需求？理解商业本质，洞察行业内涵，适合中国市场环境。每课内容简短，若有疑问请在首页提交问题，会有人来回答。&nbsp;</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 18, 5, '2014-05-22 08:39:08'),
(3, 0, 49, 1, '2', '创业课程：怎样才能高毛利', '人脉建设的要点', '尼玛', '<p>商学院都是扯淡，非商学院争取不扯淡。挣钱的第一个要领是毛利，怎样才能高毛利呢？理解商业本质，洞察行业内涵，适合中国市场环境。每课内容简短，若有疑问请在首页提交问题，会有人来回答。</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 12, 4, '2014-05-22 08:39:08'),
(4, 0, 49, 1, '2', '创业课程：怎么才能挣得快', '人脉建设的要点', '标签，淘宝', '<p>商学院都是扯淡，非商学院争取不扯淡。要想挣得快，就要找能无限放大得生意环节，靠什么能无限放大？理解商业本质，洞察行业内涵，适合中国市场环境。每课内容简短，若有疑问，请在首页提交问题，会有人来回答。</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 12, 2, '2014-05-26 08:56:24'),
(5, 0, 49, 1, '2', '创业课程：什么是商业模式', '人脉建设的要点', '', '<p>商业模式有某些人说的那么玄乎吗？说得玄乎的目的，是为了让你掏钱上他的课或买他的书，讲创业学的商学院都在扯淡。非商学院来给你讲讲几种商业模式，保证你一听就懂。&nbsp;</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 11, 3, '2014-05-26 08:56:24'),
(6, 0, 49, 1, '2', '创业课程：清晰的需求', '人脉建设的要点', '', '<p>&nbsp;世上做不起来的项目，十有八九都是需求不清晰。什么叫需求不清晰，听智选非商学院给你讲讲。</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 15, 5, '2014-05-26 08:56:24'),
(7, 0, 49, 1, '2', '创业课程：关注核心', '人脉建设的要点', '', '<p>&nbsp;创业一开始，应该关注什么？关注自己的核心，那么小饭店的核心是什么？你能搞得清楚自己的核心吗？</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 2, 1, '2014-05-26 08:56:24'),
(8, 0, 49, 1, '2', '创业课程：忘掉木桶理论', '人脉建设的要点', '', '<p>我们都是草根创业者，一眼望去都是短板。怎么办？初创企业不是木桶，而应该是，打开市场的尖刀。智选非商学院告诉你如何打造自己的尖刀。</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 2, 1, '2014-05-26 08:56:24'),
(9, 0, 49, 1, '2', '创业课程：向大佬宣战', '人脉建设的要点', '', '<p>&nbsp;营销最大的障碍是什么？是知名度吗？不是。智选非商学院告诉你，向大佬宣战，突破营销的障碍！</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 1, 1, '2014-05-26 08:56:24'),
(10, 0, 49, 1, '2', '创业课程：定位的模板', '人脉建设的要点', '', '<p>你需要让自己的项目拥有一个定位。定位是为了推广、为了销售而做的描述，让人们一听便知，跃跃欲试。如果能有一个好的定位，那你的营销就成功了一半。</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 3, 1, '2014-05-26 08:56:24'),
(11, 0, 49, 1, '2', '创业课程：第一波用户', '人脉建设的要点', '', '<p>&nbsp;第一波用户为什么而来？为什么会走？他们数量稀少，他们之中谁最值得你珍惜？谁能为你带来大众用户？</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 2, 1, '2014-05-26 08:56:24'),
(12, 0, 49, 1, '2', '创业课程：伙伴', '人脉建设的要点', '', '<p>看了前面的课，可能会认真地想想：这个项目是不是自己一个人就能做起来的。这堂课我们就来讲，先把人性看透，再去找创业伙伴。</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 1, 0, '2014-05-26 08:56:24'),
(13, 0, 49, 1, '2', '创业课程：第二波用户', '人脉建设的要点', '', '<p>&nbsp;第二波用户就是早期使用者，是不是能获得第二波用户，将决定你的项目能不能活下去。</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 1, 0, '2014-05-26 08:56:24'),
(14, 0, 49, 1, '2', '创业课程：如何研究用户', '人脉建设的要点', '', '<p>&nbsp;怎样研究用户，听取他们的意见，首先要找到核心目标用户。怎么找？28原则是用不上的。听智选来讲讲。</p>', '', 'upload/201405/1400489949.jpg', 100, 0, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 1, 1, '2014-05-26 08:56:24'),
(15, 0, 49, 1, '2', '创业课程：搭建团队的原则', '人脉建设的要点', '', '<p>&nbsp;资深的人力资源，都是先写好组织结构表、职位需求说明表，按表找人。初创者可不能这么干。根据任务寻找合适的角色，才是招人的方法。</p>', '', 'upload/201405/1400489949.jpg', 100, 1, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 0, 0, '2014-05-26 08:56:24'),
(16, 0, 49, 1, '2', '创业课程：让用户形成依赖', '人脉建设的要点', '', '<p>&nbsp;怎么才能让用户形成依赖，最重要的是什么？谈恋爱要做到：不丢人，生活质量有保证。创业项目要做到：不丢人，解决问题有保证。</p>', '', 'upload/201405/1400489949.jpg', 100, 1, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 0, 0, '2014-05-26 08:56:24'),
(17, 0, 49, 1, '2', '创业课程：搞搞形式主义', '人脉建设的要点', '', '<p>&nbsp;创业型企业必须在第二波用户面前抬高形象，玩玩腔调。你要是摆谱摆得合适，就会吸引住第二波用户。</p>', '', 'upload/201405/1400489949.jpg', 100, 1, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 0, 0, '2014-05-26 08:56:24'),
(18, 0, 49, 1, '2', '创业课程：出手要狠', '人脉建设的要点', '', '<p>&nbsp;你所做的一切都是为了挣钱，所以，出手要狠。减少不参加流转的投入，让所有投入都参加流转，更重要的是要最小化营收周期。</p>', '', 'upload/201405/1400489949.jpg', 100, 1, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 1, 0, '2014-05-26 08:56:24'),
(19, 0, 49, 1, '2', '创业课程：定价的技术', '人脉建设的要点', '', '<p>&nbsp;你的利润来源于销售价格。定价就是用消费者的三种心理绕住决策特点，心甘情愿地买单，还为你叫好。</p>', '', 'upload/201405/1400489949.jpg', 100, 1, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 0, 0, '2014-05-26 08:56:24'),
(20, 0, 49, 1, '2', '创业课程：好用户和坏用户', '人脉建设的要点', '', '<p>&nbsp;确保利润率的关键是降低客户流失率和创造优质客户，降低客户流失率并不难。要先知道他们为什么会流失。</p>', '', 'upload/201405/1400489949.jpg', 100, 1, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 1, 1, '2014-05-26 08:56:24'),
(21, 0, 49, 1, '2', '创业课程：创造优质用户', '人脉建设的要点', '', '<p>&nbsp;优质用户是谁？找到他们后，让他们成为VIP吗？不！让优质用户成为临时的特权，让所有人都有机会体验。</p>', '', 'upload/201405/1400489949.jpg', 100, 1, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 0, 0, '2014-05-26 08:56:24'),
(22, 0, 49, 1, '2', '创业课程：挑好竞争对手', '人脉建设的要点', '', '<p>&nbsp;要从竞争对手那里去抢优质用户，那么要先挑一挑竞争对手。</p>', '', 'upload/201405/1400489949.jpg', 100, 1, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 3, 0, '2014-05-26 08:56:24'),
(23, 0, 49, 1, '2', '创业课程：不快不行', '人脉建设的要点', '', '<p>&nbsp;有了升级产品，就要加快速度了，为什么一定要快呢？不快你就麻烦了。</p>', '', 'upload/201405/1400489949.jpg', 100, 1, 1, 0, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 18, 0, '2014-05-27 01:39:08'),
(24, 0, 49, 1, '2', '创业课程：快的方法', '人脉建设的要点', '', '<p>&nbsp;想快，要先把成交流程细分并标准到让用户一个动作即可完成，这样才能快。</p>', '快的方法', 'upload/201405/1400489949.jpg', 100, 1, 1, 1000, 'http://movie.ks.js.cn/flv/other/1_0.flv', '10分钟', 160, 0, '2014-05-22 08:39:08');
